import React, { Component } from 'react';

class Host extends Component {

  constructor() {
    super();
    this.state = {
      apiServerResponse: '',
      hostId: '',
    };

    this.getHostId = this.getHostId.bind(this);
  }

  componentDidMount() {
    // call getHostId
    this.getHostId().then(res => this.setState({ response: JSON.stringify(res.key) })).catch(err => console.log(err));

  };

  getHostId = async () => {
    // call server for hostId
    const response = await fetch('/api/host');
    const body = await response.json();
    if (response.status !== 200) {
      throw Error(body.message);
    };
    console.log(body.hostId);
    return body.hostId.toString();
  };

  render() {
    return (
      <h2>Host { this.state.hostId }</h2>
    );
  };

};

export default Host;
