class Host extends Component {

  constructor() {
    super();
    this.state = {
      apiServerResponse: '',
      hostID: '',
    };

    this.getHostID = this.bind
  }

  componentDidMount() {
    // call getHostID
    this.getHostID();
  };

  getHostID = async () => {
    // call server for hostID
    const response = await fetch('/api/host');
    const body = await response.json();
    if (response.status !== 200) throw Error(body.message);
    console.log(body);
  };

  render() {
    return (
      <h2>Host</h2>
    );
  };

};

export default Host;
