import React, { Component } from 'react';

// react-router-dom imports
import { Route } from 'react-router-dom';

// component definitions
const Lobby = (props) => (
  <React.Fragment>
    <h2>Join</h2>
    <form>
      <input type='text' id='room-id' />
      <input type='submit' value='Join' 
        onClick={ (event) => {
          event.preventDefault();
          let roomId = document.getElementById('room-id').value;
          props.history.push(`/join/${ roomId }`); 
        } } 
      />
    </form>
  </React.Fragment>
);

const Room = (props) => (
  <React.Fragment>
    <h2>Room { props.match.params.id }</h2>
  </React.Fragment>
);

class JoinComponent extends Component {

  render() {
    return(
      <React.Fragment>
        <Route path={`${ this.props.match.path }/:id`} component={ Room } />
        <Route exact path={ this.props.match.path } component={ Lobby } />
      </React.Fragment>
    );
  };

};

export default JoinComponent;
