import React, { Component } from 'react';
import './App.css';

// react-router-dom imports
import { BrowserRouter as Router, Route, Link } from 'react-router-dom';

// component imports
import HostComponent from './components/HostComponent.js';
import JoinComponent from './components/JoinComponent.js';

// stateless component definitions
const Header = () => <h1>react-lawful-neutral</h1>

const Home = () => (
  <React.Fragment>
    <h2>Home</h2>
    <Link to="/host">Host</Link>
    <br />
    <Link to="/join">Join</Link>
  </React.Fragment>
);

const footerStyle = {

}
const Footer = () => <small style={{  display: 'block', 
                                      bottom: '0', 
                                      position: 'fixed', 
                                      marginBottom: '1em' }}>{ new Date().toLocaleString() }</small>

// app definition
class App extends Component {

  render() {
    return (
      <Router>
        <div>
          <Header />
          <Route exact path="/" component={ Home } />
          <Route path="/host" component={ HostComponent } />
          <Route path="/join" component={ JoinComponent } />
          <Footer />
        </div>
      </Router>
    );
  }
}

export default App;
